const initialState = {
    myFavorites: []
}

const rootReducer = (state = initialState, action ) => {
    switch (action.type) {
        case "ADD_FAV":
            const newList = [...state.myFavorites, action.payload];
            return {
                ...state,
                myFavorites: newList
            }
        
        case "REMOVE_FAV":
            const updatedList = state.myFavorites.filter((favorite) =>  favorite.id !== parseInt(action.payload));

            return {
                ...state,
                myFavorites: updatedList
            }    
    
        default:
            return state;
            
    }
}

export default rootReducer;
