export default function Card({id, name, status, species, gender, origin, image,onClose}) {
   //const  = props;
   return (
      <div style={{ 
         border: '1px solid #ccc',
         borderRadius: '4px',
         padding: '10px',
         marginBottom: '10px',
         backgroundColor: '#f5f5f5',
         display: 'flex',
         flexDirection: 'column',
         alignItems: 'center'
       }}>
         <button 
           style={{ 
             position: 'absolute',
             top: '10px',
             right: '10px',
             padding: '5px',
             backgroundColor: '#fff',
             border: 'none',
             cursor: 'pointer'
           }}
           onClick={onClose}
         >
           X
         </button>
         <h2>{name}</h2>
         <h2>{status}</h2>
         <h2>{species}</h2>
         <h2>{gender}</h2>
         <h2>{origin}</h2>
         <img 
           src={image} 
           alt="imagen"
           style={{ 
             maxWidth: '100%',
             height: 'auto',
             marginTop: '10px'
           }}
         /> 
       </div>
     );
   
}
