import styles from './styles/App.module.css';
import Nav from './components/Nav/Nav';
import Cards from './components/Cards/Cards.jsx';
import About from './components/About/About'
import Detail from './components/Detail/Detail'


import { useState } from 'react'
import axios from "axios";
import { Routes, Route } from 'react-router-dom';



function App() {
   
   const [ characters, setCharacters ] = useState([])

   function onSearch(id) {
      axios(`https://rickandmortyapi.com/api/character/${id}`)
      .then(({ data }) => {
         if (data.name) {
            let idS = []; //arreglo que va a contener las id's actuales
            idS = characters.map((character) => //se guardan las id's de cada character en idS
               character.id
            )
            if (!idS.includes(data.id) ) { //si la id de data NO existe en idS
               setCharacters((oldChars) => [...oldChars, data]); //se agrega data
            }
            
         } else {
            window.alert('');
         }
      })
      .catch((error) => {
         if (error.response && error.response.status === 404) {
            window.alert('¡No hay personajes con este ID!');
         } else {
            window.alert('Ocurrió un error en la solicitud');
         }
      });
   }

   function onClose(id){
      let filteredChars = [];
      filteredChars = characters.filter((character) => 
         character.id !== parseInt(id)
      )
      
      
      setCharacters(filteredChars);
   }

   
   return (
      
      <div className={styles.mainDiv}>
         <Routes>
         <Route path="/" element={<Nav onSearch={onSearch}/>} />
        <Route path="/home" element={<Nav onSearch={onSearch}/>} />
        <Route path="/about" element={<Nav onSearch={onSearch}/>} />
        <Route path="/detail/:id" element={<Nav onSearch={onSearch}/>} />
        <Route path="/" element={<Cards characters={characters} onClose={onClose}/>} />
        <Route path="/home" element={<Cards characters={characters} onClose={onClose}/>} />
            
            
            

            <Route path="/about" element={<About />} />
            <Route path="/detail/:id" element={<Detail />} />

        
         </Routes>
      </div>
      
      
   );
}

export default App;
