import React from 'react';
import { connect } from 'react-redux';
import Cards from '../Cards/Cards'; //uso el componente Cards para renderizar al estado myFavorites
import style from "./Favorites.module.css"

export function Favorites({ myFavorites, onClose }) {
  return (
    <>
      <h1>Tus Personajes Favoritos</h1>
      
      <Cards characters={myFavorites} onClose={onClose} />
      
    </>
  );
}

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};

export default connect(mapStateToProps)(Favorites);
